                __builder2.AddAttribute(113, "ChildContent", (Microsoft.AspNetCore.Components.RenderFragment)((__builder3) => {
                    __builder3.AddMarkupContent(114, "\r\n    ");
                    __builder3.AddMarkupContent(115, "<h2>PENALIZAREA GREȘELILOR COMISE DE CANDIDAT</h2>\r\n\r\n    ");
                    __builder3.AddMarkupContent(116, "<h3>(PUNCTE DE PENALIZARE)</h3>\r\n    ");
                    __builder3.AddMarkupContent(117, @"<strong>
        Aplicate pentru nerespectarea normelor rutiere/neexecutarea ori executarea incorectă a manevrelor indicate<br>
        La examenul practic fiecare greșeală primește un număr de puncte de penalizare, dacă se cumulează 21 de puncte de penalizare la traseu, candidatul este respins.
    </strong>

    ");
                    __builder3.AddMarkupContent(118, "<h3><u><strong>Categoria B (B, B1)</strong></u></h3>\r\n\r\n    ");
                    __builder3.AddMarkupContent(119, "<div>\r\n        <h4><strong>I. PREGĂTIREA ȘI VERIFICAREA TEHNICĂ A AUTOVEHICLULUI/ANSAMBLULUI</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- neverificarea, prin intermediul aparaturii de bord sau a comenzilor autovehiculului, a functionării direcției, frânei, a instalației de ungere/ răcire, a luminilor, a semnalizării și a avertizorului sonor;<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nereglarea scaunului, a oglinzilor retrovizoare, nefixarea centurii de siguranță, neeliberarea frânei de ajutor;<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- necunoașterea aparaturii de bord sau a comenzilor autovehiculului.<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">&nbsp;</div>\r\n\r\n        <h4><strong>II. COMPORTARE ÎN TRAFIC</strong></h4>\r\n\r\n        <h4><strong>MANEVRAREA ȘI POZIȚIA AUTOVEHICULULUI ÎN TIMPUL MERSULUI</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nesincronizarea comenzilor (oprirea motorului, accelerarea excesivă, folosirea incorectă a treptelor de viteză);<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nemenținerea direcției de mers;<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- folosirea incorectă a drumului cu sau fără marcaj;<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- manevrarea incorectă la încrucișarea cu alte vehicule, inclusiv în spații restrânse;<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- întoarcerea incorectă pe o stradă cu mai multe benzi de circulație pe sens;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- manevrarea incorectă la urcarea rampelor/coborârea pantelor lungi, la circulația în tuneluri;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- folosirea incorectă a luminilor de întâlnire/luminilor de drum;<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- conducerea în mod neeconomic și agresiv pentru mediul înconjurător (turație&nbsp;excesivă, frânare/accelerare nejustificate);<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n\r\n        <h4><strong>MANEVRE SPECIALE PRIVID SIGURANȚA TRAFICULUI</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- executarea incorectă a mersul înapoi;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- executarea incorectă a întoarcerii vehiculului cu fața în sens opus prin efectuarea manevrelor de mers înainte și înapoi;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- executarea incorectă a parcării cu fața, spatele sau lateral;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- executarea incorectă a frânării cu precizie;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <h4><strong>SCHIMBAREA DIRECȚIEI DE MERS ȘI EFECTUAREA VIRAJELOR</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- neasigurarea la schimbarea direcției de mers/ la părăsirea locului de staționare;<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- executarea neregulamentară a virajelor;<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nesemnalizarea sau semnalizarea greșită a schimbării direcției de mers.<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <h4><strong>CIRCULAȚIA ÎN INTERSECȚII, POZIȚII ÎN TIMPUL MERSULUI</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- încadrarea necorespunzătoare în raport cu direcția de mers indicată;<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- efectuarea unor manevre interzise (oprire, staționare, întoarcere, mers înapoi);<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- neasigurare la pătrunderea în intersecții;<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- folosirea incorectă a benzilor la intrarea/ieșirea pe/de pe autostradă/artere similare;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nepăstrarea distanței suficiente față de cei care rulează înainte sau vin din sens opus.&nbsp;<span style=\"color:rgb(255, 0, 0)\"><strong>- 9p</strong></span></div>\r\n\r\n        <h4><strong>DEPĂȘIREA</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- ezitarea repetată de a depăși alte vehicule;<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nerespectarea regulilor de executare a depășirii ori efectuarea acestora în locuri și situații interzise.<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span></div>\r\n\r\n        <h4><strong>PRIORITATEA</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- neacordarea priorității vehiculelor și pietonilor care au acest drept (la plecarea de pe loc, în&nbsp;intersecții, sens giratoriu, stație de mijloac de transport în comun prevăzută cu alveolă, stație de tramvai fără refugiu pentru pietoni, trecere&nbsp;de pietoni);<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- tendințe repetate de a ceda trecerea vehiculelor și pietonilor care nu au prioritate;<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span></div>\r\n\r\n        <h4><strong>NERESPECTAREA SEMNIFICAȚIEI INDICATOARELOR ȘI MIJLOACELOR DE SEMNALIZARE RUTIERĂ</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nerespectarea semnificației indicatoarelor/ marcajelor/ culorilor semaforului (cu excepția culorii roșii);<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- nerespectarea semnificației culorii roșii a semaforului/ a semnalelor polițistului rutier/ a semnalelor altor persoane cu atribuții legale similare.<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span></div>\r\n\r\n        <h4><strong>VITEZA</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- depășirea vitezei maxime admise;<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- conducerea cu viteză redusă în mod nejustificat, neîncadrarea în ritmul impus de ceilalți participanți la trafic.<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span></div>\r\n\r\n        <h4><strong>CONDUCEREA PE TIMP NEFAVORABIL</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- neîndemanarea în conducere în condiții de ploaie, zăpadă, mâzgă, polei;<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- deplasarea cu viteza neadaptată condițiilor atmosferice și de drum;<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span></div>\r\n\r\n        <h4><strong>NERESPECTAREA NORMELOR LEGALE LA TRECERILE LA NIVEL CU CALEA FERATĂ.<span style=\"color:rgb(255, 0, 0)\">-21p</span></strong></h4>\r\n\r\n        <h4><strong>III. ALTE SITUAȚII CARE CONDUC LA ACORDAREA CALIFICATIVULUI \"RESPINS\"</strong></h4>\r\n\r\n        <div style=\"line-height: 20.8px;\">- prezentarea la examen sub influența băuturilor alcoolice, substanțelor sau produselor stupefiante, a medicamentelor cu efecte similare acestora sau manifestări de natură să perturbe examinarea celorlalți candidați;<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span></div>\r\n\r\n        <div style=\"line-height: 20.8px;\">- intervenția examinatorului pentru evitarea unui pericol iminent/ producerea unui eveniment rutier.<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span></div>\r\n    </div>\r\n    &nbsp;\r\n\r\n\r\n    ");
                    __builder3.AddMarkupContent(120, "<h3><strong><u>Categoria BE</u></strong></h3>\r\n\r\n    ");
                    __builder3.AddMarkupContent(121, "<h4><strong>I. PREGĂTIREA ȘI VERIFICAREA TEHNICĂ A AUTOVEHICLULUI/ANSAMBLULUI</strong></h4>\r\n\r\n    ");
