                __builder2.AddAttribute(148, "ChildContent", (Microsoft.AspNetCore.Components.RenderFragment)((__builder3) => {
                    __builder3.AddMarkupContent(149, "\r\n    ");
                    __builder3.AddMarkupContent(150, "<h2><strong>PENALIZAREA GREȘELILOR COMISE DE CANDIDAT</strong></h2>\r\n\r\n    ");
                    __builder3.AddMarkupContent(151, "<h3><strong>(PUNCTE DE PENALIZARE)</strong></h3>\r\n    ");
                    __builder3.AddMarkupContent(152, @"<strong>
        Aplicate pentru nerespectarea normelor rutiere/neexecutarea ori executarea incorectă a manevrelor indicate<br>
        La examenul practic fiecare greșeală primește un număr de puncte de penalizare, dacă se cumulează 21 de puncte de penalizare la traseu, candidatul este respins.
    </strong>

    ");
                    __builder3.AddMarkupContent(153, "<h3><u><strong>Categoria C (C,&nbsp;CE, C1, C1E, Tr)</strong></u></h3>\r\n    ");
                    __builder3.AddMarkupContent(154, @"<strong>I. PREGĂTIREA ȘI VERIFICAREA TEHNICĂ A AUTOVEHICLULUI/ ANSAMBLULUI DE VEHICULE</strong><br>
    - neefectuarea controlului vizual, în ordine aleatorie privind: starea anvelopelor, fixarea roților (starea piulițelor), starea elementelor suspensiei, a rezervoarelor de aer, a parbrizului, ferestrelor, a fluidelor (ulei motor, lichid răcire, fluid spălare parbriz), a blocului de lumini/ semnalizare față/ spate, catadioptrii, trusa medicală, triunghiul&nbsp;reflectorizant, stingător de incendiu;");
                    __builder3.AddMarkupContent(155, "<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span><br>\r\n    - neefectuarea controlului caroseriei, a învelișului ușilor pentru marfă, a mecanismului de încărcare, a fixării încărcăturii&nbsp;");
                    __builder3.AddMarkupContent(156, @"<strong>(numai pentru&nbsp;C, CE, C1, C1E, Tr);<span style=""color:rgb(255, 0, 0)"">-9p</span></strong><br>
    - neverificarea, prin intermediul aparaturii de bord sau a comenzilor autovehiculului, a functionării direcției, frânei, instalației de ungere/ răcire, a luminilor, a semnalizării și a avertizorului sonor;");
                    __builder3.AddMarkupContent(157, "<strong><span style=\"color:rgb(255, 0, 0)\">-3p</span></strong><br>\r\n    - necunoașterea aparaturii de înregistrare a activității conducătorului auto (Cu exceptia C1 C1E Tr care nu intră&nbsp;în domeniul Regulamentului (CEE) nr 3,821/85);");
                    __builder3.AddMarkupContent(158, "<strong><span style=\"color:rgb(255, 0, 0)\">-3p</span></strong><br>\r\n    - neverificarea dispozitivului de cuplare și a conexiunilor instalației de frânare/ electrice, a catadioptrilor&nbsp;");
                    __builder3.AddMarkupContent(159, "<strong>(numai pentru CE, C1E, D1E, DE, Tr);</strong>");
                    __builder3.AddMarkupContent(160, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - neverificarea caroseriei, a ușilor de serviciu, a ieșirilor de urgență, a echipamentul de prim-ajutor, a stingatoarelor de incendiu și a altor echipamente de siguranță&nbsp;");
                    __builder3.AddMarkupContent(161, "<strong>(numai pentru D, DE, D1, D1E, Tb,Tv);<span style=\"color:rgb(255, 0, 0)\">-5p</span></strong><br>\r\n    - nereglarea scaunului, a oglinzilor retrovizoare, nefixarea centurii de siguranță, neeliberarea frânei de ajutor;");
                    __builder3.AddMarkupContent(162, "<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span><br>\r\n    - necunoașterea aparaturii de bord sau a comenzilor autovehiculului.");
                    __builder3.AddMarkupContent(163, "<strong><span style=\"color:rgb(255, 0, 0)\">-3p</span></strong>\r\n\r\n    ");
                    __builder3.AddMarkupContent(164, "<h4><strong>II. MANEVRE&nbsp;SPECIALE PRIVIND SIGURANȚA&nbsp;TRAFICULUI (se penalizează executarea incorectă a acestora)</strong></h4>\r\n    - cuplarea unei remorci de autovehiculul trăgător din/ cu revenire la poziția inițială în staționare paralel;");
                    __builder3.AddMarkupContent(165, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - mersul înapoi;");
                    __builder3.AddMarkupContent(166, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - parcarea în siguranță, cu fața/ cu spatele/ laterală, pentru încărcare/ descărcare, sau la o rampă/ platformă de încărcare, sau la o instalație similară;");
                    __builder3.AddMarkupContent(167, "<strong><span style=\"color:rgb(255, 0, 0)\">-7p</span></strong><br>\r\n    - oprirea pentru a permite călătorilor urcarea/ coborârea în/ din autobuz/ tramvai/ troleibuz, în siguranță.&nbsp;");
                    __builder3.AddMarkupContent(168, "<strong>(numai pentru D, DE, D1, D1E, Tb,Tv).</strong>");
                    __builder3.AddMarkupContent(169, "<span style=\"color:rgb(255, 0, 0)\"><strong>-7p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(170, "<h4><strong>III. COMPORTARE ÎN TRAFIC</strong></h4>\r\n\r\n    ");
                    __builder3.AddMarkupContent(171, "<h4><strong>MANEVREAREA ȘI POZIȚIA AUTOVEHICULULUI ÎN TIMPUL MERSULUI</strong></h4>\r\n    - nesincronizarea comenzilor (oprirea motorului, accelerarea excesivă, folosirea incorectă a treptelor de viteză);");
                    __builder3.AddMarkupContent(172, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - nemenținerea direcției de mers;");
                    __builder3.AddMarkupContent(173, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - folosirea incorectă a drumului, cu sau fără marcaje;");
                    __builder3.AddMarkupContent(174, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - manevrarea incorectă la încrucișarea cu alte vehicule, inclusiv în spații restrânse;");
                    __builder3.AddMarkupContent(175, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - executarea incorectă a mersului înapoi, a parcării cu fața, spatele sau lateral;");
                    __builder3.AddMarkupContent(176, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - executarea incorectă a întoarcerii vehiculului cu fața în sens opus prin efectuarea manevrelor de mers înainte și înapoi;");
                    __builder3.AddMarkupContent(177, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - întoarcerea incorectă pe o stradă cu mai multe benzi de circulație pe sens;");
                    __builder3.AddMarkupContent(178, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - manevrarea incorectă la urcarea rampelor/ coborârea pantelor lungi, la circulația în tuneluri;");
                    __builder3.AddMarkupContent(179, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - folosirea incorectă a luminilor de întâlnire/ luminilor de drum;");
                    __builder3.AddMarkupContent(180, "<span style=\"color:rgb(255, 0, 0)\"><strong>-3p</strong></span><br>\r\n    - conducerea în mod neeconomic și agresiv pentru mediul înconjurător (turație excesivă, frânare/ accelerare nejustificate).");
                    __builder3.AddMarkupContent(181, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(182, "<h4><strong>SCHIMBAREA DIRECȚIEI DE MERS ȘI EFECTUAREA VIRAJELOR</strong></h4>\r\n    - neasigurarea la schimbarea direcției de mers/ părăsirea locului de staționare;");
                    __builder3.AddMarkupContent(183, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - executarea neregulamentară a virajelor;");
                    __builder3.AddMarkupContent(184, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - nesemnalizarea sau semnalizarea greșită a schimbării direcției de mers.");
                    __builder3.AddMarkupContent(185, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(186, "<h4><strong>CIRCULAȚIA ÎN INTERSECȚII, POZIȚII ÎN TIMPUL MERSULUI</strong></h4>\r\n    - încadrarea necorespunzătoare în raport cu direcția de mers indicată;");
                    __builder3.AddMarkupContent(187, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - efectuarea unor manevre interzise (oprire, staționare, întoarcere, mers înapoi);");
                    __builder3.AddMarkupContent(188, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - neasigurarea la pătrunderea în intersecții;");
                    __builder3.AddMarkupContent(189, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - folosirea incorectă a benzilor de intrare/ ieșire pe/ de pe autostradă/ artere similare;");
                    __builder3.AddMarkupContent(190, "<span style=\"color:rgb(255, 0, 0)\"><strong>-5p</strong></span><br>\r\n    - nepăstrarea distanței suficiente față de cei care rulează înainte sau vin din sens opus.");
                    __builder3.AddMarkupContent(191, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(192, "<h4><strong>DEPĂȘIREA</strong></h4>\r\n    - ezitarea repetată de a depăși alte vehicule;");
                    __builder3.AddMarkupContent(193, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span><br>\r\n    - nerespectarea regulilor de executare a depășirii ori efectuarea acesteia în locuri și situații interzise.");
                    __builder3.AddMarkupContent(194, "<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(195, @"<h4><strong>PRIORITATEA</strong></h4>
    - neacordarea priorității vehiculelor și pietonilor care au acest drept (la plecarea de pe loc, în intersecții, sens giratoriu, stație de mijloac&nbsp;de transport în comun prevăzută cu alveolă, stație de tramvai fără refugiu pentru pietoni, trecere de pietoni);");
                    __builder3.AddMarkupContent(196, "<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span><br>\r\n    - tendințe repetate de a ceda trecerea vehiculelor și pietonilor care nu au prioritate.");
                    __builder3.AddMarkupContent(197, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(198, "<h4><strong>NERESPECTAREA SEMNIFICAȚIEI INDICATOARELOR ȘI MIJLOACELOR DE SEMNALIZARE RUTIERĂ</strong></h4>\r\n    - nerespectarea semnificiației indicatoarelor/ marcajelor/ culorilor semaforului (cu excepția culorii roșii);");
                    __builder3.AddMarkupContent(199, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - nerespectarea semnificației culorii roșii a semaforului/ semnalelor polițistului rutier/ semnalelor altor persoane cu atribuții legale similare.");
                    __builder3.AddMarkupContent(200, "<span style=\"color:rgb(255, 0, 0)\"><strong>-21p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(201, "<h4><strong>VITEZA</strong></h4>\r\n    - depășirea vitezei legale maxime admise;");
                    __builder3.AddMarkupContent(202, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - conducerea cu viteză redusă în mod nejustificat, neîncadrarea în ritmul impus de ceilalți participanți la trafic.");
                    __builder3.AddMarkupContent(203, "<span style=\"color:rgb(255, 0, 0)\"><strong>-6p</strong></span>\r\n\r\n    ");
                    __builder3.AddMarkupContent(204, "<h4><strong>CONDUCEREA PE TIMP NEFAVORABIL</strong></h4>\r\n    - neîndemânarea în conducere în condiții de ploaie, zăpadă, mâzgă, polei;");
                    __builder3.AddMarkupContent(205, "<span style=\"color:rgb(255, 0, 0)\"><strong>-9p</strong></span><br>\r\n    - deplasarea cu viteza neadaptată condițiilor atmosferice și de drum;");
