                    __builder3.AddMarkupContent(207, "<h4><strong>NERESPECTAREA NORMELOR LEGALE LA TRECERILE LA NIVEL CU CALEA FERATĂ.<span style=\"color:rgb(255, 0, 0)\">-21p</span></strong></h4>\r\n\r\n    ");
